import React, { Component } from "react";
import Column from "./column";

class ColumnContainer extends React.Component {
  state = {
    tableName: "",
    attributes: [
      {
        name: "",
        type: "",
        maxSize: ""
      }
    ]
  };
  URL = "/api/table";

  handleCreate = () => {
    this.postData = this.postData.bind(this);
    this.postData("http://localhost:5000/api/table").then(data => {
      console.log(data);
    });
  };

  async postData(url) {
    const data = this.state;
    const options = {
      method: "POST",
      mode: "no-cors",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(data)
    };

    console.log(options);

    const response = await fetch(url, options);
    return await response;
  }

  handleChange = idx => e => {
    const { name, value } = e.target;
    const attributes = [...this.state.attributes];
    attributes[idx][name] = value;
    this.setState({
      attributes
    });
  };

  handleChangeOnTableName = e => {
    const { value } = e.target;
    this.setState({
      tableName: value
    });
  };

  handleAddRow = () => {
    const item = {
      name: "",
      type: "",
      maxSize: ""
    };
    this.setState({
      attributes: [...this.state.attributes, item]
    });
  };
  handleRemoveRow = () => {
    this.setState({
      attributes: this.state.attributes.slice(0, -1)
    });
  };
  handleRemoveSpecificRow = idx => () => {
    const attributes = [...this.state.attributes];
    attributes.splice(idx, 1);
    this.setState({ attributes });
  };
  render() {
    console.log(this.state);
    return (
      <div>
        <div className="container">
          <div className="row clearfix">
            <div className="col-md-12 column">
              <label className="h3 text-center">
                Table name{" "}
                <input
                  type="text"
                  name="tableSize"
                  value={this.state.tableName}
                  onChange={this.handleChangeOnTableName}
                  className="form-control"
                />
              </label>
              <table
                className="table table-bordered table-hover table-striped"
                id="tab_logic"
              >
                <thead>
                  <tr>
                    <th className="text-center"> # </th>
                    <th className="text-center"> Name </th>
                    <th className="text-center"> Type </th>
                    <th className="text-center"> Max Size</th>
                    <th />
                  </tr>
                </thead>
                <tbody>
                  {this.state.attributes.map((item, idx) => (
                    <tr id="addr0" key={idx}>
                      <td>{idx}</td>
                      <td>
                        <input
                          type="text"
                          name="name"
                          value={this.state.attributes[idx].name}
                          onChange={this.handleChange(idx)}
                          className="form-control"
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          name="type"
                          value={this.state.attributes[idx].type}
                          onChange={this.handleChange(idx)}
                          className="form-control"
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          name="maxSize"
                          value={this.state.attributes[idx].maxSize}
                          onChange={this.handleChange(idx)}
                          className="form-control"
                        />
                      </td>
                      <td>
                        <button
                          className="btn btn-outline-danger btn-sm"
                          onClick={this.handleRemoveSpecificRow(idx)}
                        >
                          Remove
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <button
                onClick={this.handleAddRow}
                className="btn btn-primary m-2"
              >
                Add Row
              </button>
              <button
                onClick={this.handleRemoveRow}
                className="btn btn-danger float-right m-2"
              >
                Delete Last Row
              </button>
              <br />
              <button
                onClick={this.handleCreate}
                className="btn btn btn-success m-2 btn-lg"
              >
                Create Table
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ColumnContainer;
