import React, { Component } from "react";
import TableNameContainer from "./TableNameSidebar";
import TableDataContainer from "./TableDataContainer";

class ViewDataApp extends Component {
  state = {
    currentTableName: ""
  };

  parentGetClickedTable = tableName => {
    this.setState({ currentTableName: tableName });
  };

  render() {
    console.log("app: ", this.state.currentTableName);
    return (
      <React.Fragment>
        <TableNameContainer
          parentGetClickedTable={this.parentGetClickedTable}
        />
        <TableDataContainer tableName={this.state.currentTableName} />
      </React.Fragment>
    );
  }
}

export default ViewDataApp;
